<!doctype html><html lang="en">	<head>		<meta charset="UTF-8">		<title>List Of Barang</title>		<script src="<?php echo base_url()?>template/js/jquery-1.11.1.min.js"></script>		<script src="<?php echo base_url()?>template/js/jquery.dataTables.min.js"></script>		<link rel="stylesheet" href="<?php echo base_url()?>template/css/jquery.dataTables.css">			<script>			$(document).ready(function() {							var table = $('#example').DataTable();				$('#example tbody').on( 'click', 'tr', function () {					if ( table.$('tr.selected').removeClass('selected') ) {						$(this).addClass('selected');					}				} );								$('#example tbody').on( 'click', 'tr', function () {				  var rowData = table.row( this ).data();					//alert(rowData);					$('#barang_codes').val(rowData[0]);					$('#barang_name').val(rowData[1]);				} );								/*$('#button').click( function () {					alert($('#nama').val());				} );*/			} );						function closeWin() {				var barang_code = document.form2.barang_codes.value;				var barang_name = document.form2.barang_name.value;								window.opener.document.form1.barang_code.value = barang_code; 				window.opener.document.form1.barang_name.value = barang_name;				self.close();				//window.opener.document.getElementById("tret").innerHTML = agen;			}						function closeWindow() {				window.close();			}					</script>	</head>	<body>			<h2>List Of Barang</h2>		<form action="" method="post" name="form2" id="form2">			<input type="text" id="barang_codes" name="barang_codes">			<input type="hidden" id="barang_name" name="barang_name">			<input type="hidden" id="satuan_code" name="satuan_code">			<input type="hidden" id="saldo_awal" name="saldo_awal">			<table id="example" class="table table-hover">				<thead>					<tr>						<th>Kode Barang</th>						<th>Nama Barang</th>						<th>Kode Satuan</th>						<th>Kode Hs</th>						<th>Kategory Barang</th>						<th style="display: none;">Quantity</th>					</tr>				</thead>				<tbody>					<?php foreach($barang as $barang) { ?>					<tr>						<td><?php echo $barang->barang_code?></td>						<td><?php echo $barang->barang_name?></td>						<td><?php echo $barang->satuan_code?></td>						<td><?php echo $barang->hs_code;?></td>						<td><?php echo $barang->barang_category;?></td>						<td style="display: none;"><?php echo $barang->saldo_awal;?></td>						<?php } ?>					</tr>				</tbody>			</table>			<div class="form-group">			<div class="col-sm-2"></div>			<div class="col-sm-3" align="right">				<button type="button" class="btn btn-default" style="height:27px; width:75px;" onclick="closeWindow();">Close</button>				<input type="submit" value="Submit" class="btn btn-primary" style="height:27px; width:75px;" onclick="closeWin();">			</div>		</div>		</form>		</body></html>